function Produs() {
    const [prod, setProdus] = useState(null);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const produsId = urlParams.get('id');

    // Fetch product details based on the productId
    // Use productId to fetch the specific product data from your API
    // Example: axios.get(`/api/products/${productId}`)
    // Once you fetch the product data, set it in the state
    // setProduct(response.data);

    // For now, let's simulate setting the product data
    const simulatedProduct = { id: produsId, name: 'Product Name', price: 10.99 };
    setProdus(simulatedProduct);
  }, []);

  if (!prod) {
    return <h1>Loading...</h1>;
  }

  return (
    <div>
      <h1>{prod.name}</h1>
      <p>Price: {prod.price}</p>
      {/* Rest of the product details */}
    </div>
  );

}

export default Produs;
