import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Produs() {
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const productId = urlParams.get('id');

    // Fetch product details based on the productId
    axios.get(`/api/fetes/${productId}`)
      .then((response) => {
        setProduct(response.data);
      })
      .catch((error) => {
        console.error('Error fetching product details:', error);
      });

      axios.get(`/api/baietis/${productId}`)
      .then((response) => {
        setProduct(response.data);
      })
      .catch((error) => {
        console.error('Error fetching product details:', error);
      });

      axios.get(`/api/figurines/${productId}`)
      .then((response) => {
        setProduct(response.data);
      })
      .catch((error) => {
        console.error('Error fetching product details:', error);
      });

      axios.get(`/api/plusuris/${productId}`)
      .then((response) => {
        setProduct(response.data);
      })
      .catch((error) => {
        console.error('Error fetching product details:', error);
      });

      axios.get(`/api/accesoriis/${productId}`)
      .then((response) => {
        setProduct(response.data);
      })
      .catch((error) => {
        console.error('Error fetching product details:', error);
      });
  }, []);
  

  if (!product) {
    return <h1>Loading...</h1>;
  }

  return (
    <div className='produss'>
        <section id='prod-detalii' className='detalii'>
            <div className='poze'>
                <img src={product.img} width="100%" id='prima-img' alt={product.nume} />
                <img src={product.img2} width="100%" id='prima-img' alt={product.nume} />
            </div>
            <div></div>
        </section>
      <h1>{product.nume}</h1>
      
      <p>Description: {product.descriere}</p>
      <p>Price: {product.pret} {product.moneda}</p>
    </div>
  );
}

export default Produs;
